using eTickets.Controllers;using Microsoft.EntityFrameworkCore;namespace eTickets.Date{    public class AppDbContext:DbContext    {        public AppDbContext(DbContextOptions<AppDbContext>  option ):base (option)    //傳遞給option，option會傳給DbContext。option是DbContextOptions類別，內部只會傳遞AppDbContext類別的資料        {        }        protected override void OnModelCreating(ModelBuilder modelBuilder)        {            modelBuilder.Entity<Movie_Actor>().HasKey(e => new              {                e.ActorId,                e.MovieId            }) ;            modelBuilder.Entity<Movie_Actor>().HasOne(e => e.Movie).WithMany(e =>e.Movie_Actor).HasForeignKey(e => e.MovieId);            modelBuilder.Entity<Movie_Actor>().HasOne(e => e.Actor).WithMany(e => e.Movie_Actor).HasForeignKey(e =>e.ActorId );            base.OnModelCreating(modelBuilder);        }        public DbSet<Actor> Actors { get; set; }        public DbSet<Cinemas> Cinemas { get; set; }        public DbSet<Movie_Actor> Movie_Actors { get; set; }        public DbSet<Movies> Movies { get; set; }        public DbSet<Productor> Productors { get; set; }    }}